import React, { useEffect, useState } from "react";
import { Link, Redirect } from "react-router-dom";

const InstructorInfo = ({ setAuth }) => {
  const [info, setInfo] = useState("");
  const [id, setId] = useState("");

  const getInfo = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/instructor/:id", {
        method: "GET",
        // headers: { jwt_token: localStorage.token }
      });

      const parseData = await res.json();
      setInfo(parseData.info);
    } catch (err) {
      console.error(err.message);
    }
  };
  const logout = async e => {
    e.preventDefault();
    try {
      localStorage.removeItem("token");
      setAuth(false);
      //toast.success("Logout successfully");
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getInfo();
  }, []);

//   console.log(Data.courses[0].id)
//   console.log(instructors)
  return (
    <div>
      <h1>{info.info.name}</h1>
      {/* <ol>
        {instructors && instructors.map(data => <li><Link to={"/instructor/" + data.id}>
          {data.name}</Link> Department : {data.dept_name}
        </li>)}
      </ol>  */}
    </div>
    
  );
};

export default InstructorInfo;
